import './App.css';
import axios from 'axios';
import React, { useState } from 'react';


function App() {

  const [isHovering, setIsHovering] = useState(false);
  const [fact, setFact ] = useState('');

  const handleClick = () => {
    const options = {
      method: 'GET',
      url: 'https://api.api-ninjas.com/v1/randomuser',
      headers: {
        'X-Api-Key': 'rYZpj+cX3gxRKnKg5eEB/w==JZ6ELBj7e8BcbTLw',
      }
    };

    axios.request(options)
      .then(response => setFact(response.data))
      .catch(error => console.error(error));
  };

  return (
    <div className="App" id = "App">
        <div class = "Main" className = {`Main${isHovering ? ' shadow-lg p-3 mb-5 bg-body-tertiary rounded' : ''}`}
          onMouseEnter={() => setIsHovering(true)}
          onMouseLeave={() => setIsHovering(false)}>
        <button class = "generate-button" onClick={handleClick}> Get a random fact </button>
        <p> {JSON.stringify(fact)} </p>
        </div>
    </div>
  );
}

export default App;
