import './App.css';
import axios from 'axios';
import React, { useState } from 'react';


function App() {

  const [isHovering, setIsHovering] = useState(false);
  const [fact, setFact ] = useState('');

  const handleClick = () => {
    const options = {
      method: 'GET',
      url: 'https://api.api-ninjas.com/v1/randomuser',
      headers: {
        'X-Api-Key': 'rYZpj+cX3gxRKnKg5eEB/w==JZ6ELBj7e8BcbTLw',
      }
    };

    axios.request(options)
    .then(response => {
      const { name, address, email, birthday } = response.data;
      setFact({ name, address, email, birthday });
    })
      .catch(error => console.error(error));
  };

  return (
    <div className="App" id = "App">
        <div className = {`Main${isHovering ? ' shadow-lg mb-5 bg-body-tertiary rounded' : ''}`}
          onMouseEnter={() => setIsHovering(true)}
          onMouseLeave={() => setIsHovering(false)}>
        <button class = "generate-button" onClick={handleClick}> Generate a User </button>
        <ul style = 'margin-top: 3rem;' >
            {Object.values(fact).map((value, index) => (
              <li key={index}>{value}</li>
            ))}
          </ul>
        </div>
    </div>
  );
}

export default App;
